let current = "start";
let voice = null;

function selectVoice() {
  const voices = speechSynthesis.getVoices();
  voice = voices.find(v => v.lang === "en-US" && /female/i.test(v.name)) || voices.find(v => v.lang === "en-US");
}
speechSynthesis.onvoiceschanged = selectVoice;

function speakScene() {
  if (!voice) selectVoice();
  let msg = new SpeechSynthesisUtterance(scenes[current].text);
  msg.voice = voice;
  msg.rate = 0.97;
  speechSynthesis.speak(msg);
}

function render() {
  let s = scenes[current];
  document.getElementById("sceneText").innerHTML = s.text;

  if(s.end){
    document.getElementById("choices").innerHTML = "";
    document.getElementById("end").style.display = "block";
  } else {
    document.getElementById("choices").innerHTML =
      s.choices.map(c => `<button onclick="go('${c.goto}')">${c.label}</button>`).join("");
  }

  speakScene();
}

function go(next){ speechSynthesis.cancel(); current=next; render(); }
