<script>
const irregulars = [
"went","gone","go","ate","eaten","eat","saw","seen","see","took","taken","take",
"gave","given","give","came","come","drank","drunk","drink","ran","run","spoke","spoken","speak",
"wrote","written","write","felt","feel","found","find","knew","known","know","began","begun","begin",
"broke","broken","break","bought","buy","brought","bring","built","build","chose","chosen","choose",
"did","done","do","drove","driven","drive","fell","fallen","fall","flew","flown","fly","got","gotten","get",
"heard","hear","kept","keep","lost","lose","made","make","paid","pay","read","read","rode","ridden","ride",
"sold","sell","sent","send","sat","sit","slept","sleep"
];

// (D+E+F) detección, conteo y sugerencias
function processText(){
  const raw = studentText.value;
  const words = raw.toLowerCase().replace(/[^a-z\s]/g,"").split(/\s+/);
  const matches = words.filter(w => irregulars.includes(w));

  document.getElementById("count").textContent = matches.length;

  // Verbos únicos
  const unique = [...new Set(matches)];
  document.getElementById("uniqueCount").textContent = unique.length;

  // Resaltado
  document.getElementById("highlightBox").innerHTML = raw.split(/\s+/).map(word=>{
    const clean = word.toLowerCase().replace(/[^a-z]/g,"");
    return irregulars.includes(clean) ? `<span style="background:yellow;">${word}</span>` : word;
  }).join(" ");

  // Mostrar lista de verbos usados
  document.getElementById("usedList").textContent = unique.join(", ") || "(ninguno)";

  // Sugerencias por repetición
  let suggestions = "";
  unique.forEach(v=>{
    const count = matches.filter(x=>x===v).length;
    if(count > 2){
      suggestions += `Estás usando mucho <strong>${v}</strong>. Intenta variar.<br>`;
    }
  });
  document.getElementById("suggestions").innerHTML = suggestions;
}

// Envío sólo si hay >= 6 verbos únicos
function sendMail(){
  const name = studentName.value.trim();
  const email = studentEmail.value.trim();
  const text = studentText.value.trim();
  const audio = audioLink.value.trim();

  const uniqueCount = parseInt(document.getElementById("uniqueCount").textContent);

  if(uniqueCount < 6){
    verbCheck.style.display="block";
    verbCheck.textContent="⚠️ Necesitas al menos 6 verbos irregulares distintos. Tienes: " + uniqueCount;
    return;
  }

  const teacher = "jose.torres.osorio@gmail.com";
  const subject = encodeURIComponent("Entrega OVA — " + name);
  let body = `Nombre: ${encodeURIComponent(name)}%0D%0A`;
  body += `Correo: ${encodeURIComponent(email)}%0D%0A%0D%0A`;
  body += `Texto:%0D%0A${encodeURIComponent(text)}%0D%0A%0D%0A`;
  body += `Audio: ${encodeURIComponent(audio || "(no adjunto)")}%0D%0A`;

  window.location.href = `mailto:${teacher}?subject=${subject}&body=${body}`;
  result.textContent="✔ Se abrió tu correo. Envía para completar la entrega.";
}

function speakText(){
  const msg = new SpeechSynthesisUtterance(studentText.value);
  msg.lang = "en-US";
  msg.rate = 0.95;
  speechSynthesis.speak(msg);
}
